import { useState } from "react";

const Example = () => {

  const [ countA, setCountA] = useState(0)
  return (
    <>
      <p>ボタンAを{countA}回押しました！</p>
      <button onClick={() => {
        setCountA
      }}>ボタンA</button>
      <p>ボタンBを{}回押しました！</p>
      <button>ボタンB</button>
      <p>ボタンCを{}回押しました！</p>
      <button>ボタンC</button>
    </>
  )

};

export default Example;
