import {useState} from "react";

const Example = () => {
  const [fruit, setFruit] = useState('Apple');
  const onChange = (e) => setFruit(e.target.value);

    return (
      <>
          <label>
            <input
              type="radio"
              value="Apple"
              checked={fruit === "Apple"}
              onChange={onChange}
            >Apple</input>
          </label>
          <label>
            <input
              type="radio"
              value="Banana"
              checked={fruit === "Banana"}
              onChange={onChange}
            >Banana</input>
          </label>
          <label>
            <input
              type="radio"
              value="Apple"
              checked={fruit === "Cherry"}
              onChange={onChange}
            >Cherry</input>
          </label>
          <label>
        <input
          type="radio"
          value="Banana"
          checked={fruit === "Banana"}
          onChange={onChange}
        />
        Banana
      </label>
      <label>
        <input
          type="radio"
          value="Cherry"
          checked={fruit === "Cherry"}
          onChange={onChange}
        />
        Cherry
      </label>
          <br />
          <h3>私は{fruit}がたべたい</h3>
      </>
  );
};

export default Example;
