import {useState} from "react";

const Example = () => {
  const [fruit, setFruit] = useState('Apple');
  const onChange = (e) => setFruit(e.target.value);

    return (
      <>
        <div style={{ textAlign: "center" }}>
          <label>
            <input
              type="radio"
              value="Apple"
              checked={fruit === "Apple"}
              onChange={onChange}
            >Apple</input>
          </label>
          <label>
            <input
              type="radio"
              value="Banana"
              checked={fruit === "Banana"}
              onChange={onChange}
            >Banana</input>
          </label>
          <label>
            <input
              type="radio"
              value="Apple"
              checked={fruit === "Cherry"}
              onChange={onChange}
            >Cherry</input>
          </label>
          <br />
          <h3>私は{fruit}がたべたい</h3>
        </div>
      </>
  );
};

export default Example;
