import { useState } from "react";


const Example = () => {
  const [val, setVal] = useState("");
  const clearVal = () => setVal("");
  return (
    <p style={{ textAlign: "center" }}>
      <label htmlFor="456">ラベル</label>
      <input
        id="123"
        placeholder="こんにちは"
        value={val}
        onChange={(e) => setVal(e.target.val)}
      ></input>
      <br />
      練習用に使ってください！
    </p>
  );
};

export default Example;
