import { VStack, StackDivider, HStack, IconButton } from "@chakra-ui/react"
import {VscCheck} from "react-icons"

const List = ({todos, deleteTodo}) => {
    const complete = (id) => {
        deleteTodo(id)
    }
    return (
        <VStack
            divider={<StackDivider  />}
            // color={{ sm: 'red.600', md: 'blue.600', lg: 'green.600'}}
            borderColor="black.200"
            borderWidth="1px"
            borderRadius="3px"
            p={5}
            alignItems="start"
        >
            {todos.map(todo => {
                return (
                    <HStack key={todo.id} spacing="5">
                        <IconButton onClick={() => complete(todo.id)}>完了</IconButton>
                        <span>{todo.content}</span>
                    </HStack>
                )
            })}
        </VStack>
    );
}

export default List;
