const counter = createSlice({
  name: 'counter',
  initialState: {
    count:0
  },
  reducers: {
    add(state, { type, payload }) {
      // console.log(type, payload)
      // const newState = { ...state};
      // newState.count += payload;
      // return newState;

    },
    minus(state, { type, payload }) {
      console.log(type, payload)
      const newState = { ...state};
      newState.count -= payload;
      return newState;
    }
  }
})

const { add, minus } = counter.actions;

export { add, minus }
export default counter.reducer
