import { createStore} from "redux";

const initialState = 0;
const reducer = (statea = initialState, { type, step }) => {
  switch (type) {
    case "+":
      return statea + step;
    case "-":
      return state - step;
    default:
      return state;
  }
}

export default createStore(
  reducer
);
